static bool is_valid_for_everyone(CHAR_DATA *) {return true;}
static bool is_valid_for_greater_fire(CHAR_DATA * ch) {return (ch->class_num == global_int_class_firescholar && ch->pcdata != NULL && ch->pcdata->minor_sphere == SPH_FIRE);}
static bool is_valid_for_greater_spirit(CHAR_DATA * ch) {return (ch->class_num == global_int_class_spiritscholar && ch->pcdata != NULL && ch->pcdata->minor_sphere == SPH_SPIRIT);}
static bool is_valid_for_greater_water(CHAR_DATA * ch) {return (ch->class_num == global_int_class_waterscholar && ch->pcdata != NULL && ch->pcdata->minor_sphere == SPH_WATER);}
static bool is_valid_for_greater_air(CHAR_DATA * ch) {return (ch->class_num == global_int_class_airscholar && ch->pcdata != NULL && ch->pcdata->minor_sphere == SPH_AIR);}
static bool is_valid_for_greater_earth(CHAR_DATA * ch) {return (ch->class_num == global_int_class_earthscholar && ch->pcdata != NULL && ch->pcdata->minor_sphere == SPH_EARTH);}
static bool is_valid_for_greater_void(CHAR_DATA * ch) {return (ch->class_num == global_int_class_voidscholar && ch->pcdata != NULL && ch->pcdata->minor_sphere == SPH_VOID);}

